


########################################################################################
#
#######################################################################################
class SiLog:
        
    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def Message(msg: str):
        print("{}".format(msg))

    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def Message(msg: str, val: str):
        print("{}".format(msg + " [" + str(val) + "]"))


    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def Error(msg: str, val):
        raise Exception(msg + " [" + str(val) + "]")

    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def Unsupported(val):
        raise Exception("unsupported [{}]".format(val))
    

    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def KeyVal(key: str, val: str):
        print("{} [{}]".format(key, val))

