import os
import random


########################################################################################
#
#######################################################################################
class SiRandom:
        
    ########################################################################################
    #
    #######################################################################################
    @staticmethod
    def Seed(seed):

        #seed 0 means use random based on system time 
        #python does this by default - so no need to do anyything
        if seed == 0:
            SiLog.KeyVal("random seed", "based on time")  
            return

        SiLog.KeyVal("random seed", seed)   

        random.seed(seed)


    ########################################################################################
    #
    # return [0.0 to 1.0)
    #
    #######################################################################################
    @staticmethod
    def GetFloat():
        return random.random()


    ########################################################################################
    #
    # return [0.0 to 1.0]
    #
    #######################################################################################
    @staticmethod
    def GetInt(min, max):
        return random.randint(min, max)

